graphql.query:
query {
    getDoctor(
        doctorId: "623a4d21b1332b5c4c79e5a7"
  ) {
        _id
        fullName
        age
        specialization
    }
}

query {
    addDoctor(
        input: { 
            fullName: "Test fullName"
            age: 28
            specialization: "Dentist"
            sex: "M"
        }
    ) {
        fullName
        age
        specialization
        sex
    }
}

mutation {
    removeDoctor(
        doctorId: "623a50e5a55b2e5e6a9374d2"
  ) {
        fullName
        age
        specialization
    }
}

mutation {
    createAvailability(
        input: { 
            doctorId: "623a4be15eec415b89ed269"
            date: "2022-03-24"
            timeslots: [{
                fromTime: "8:14"
                toTime: "14:34"
            },{
                fromTime: "15:00"
                toTime: "17:13"
            }
            ]
        }
    ) {
        _id
        doctorId
        date
        fromTime
        toTime
    }
}

query {
    getAllAvailability(
        input: { 
            doctorId: "623a4be15eec415b89ed269"
            fromDate: "2022-03-24"
            toDate: "2022-03-24"
            duration: 30
        }
    )  {
            doctorId
            date
            fromTime
            toTime
    }
}

query {
    getConsultations(
        input: { 
            doctorId: "623a4be15eec415b89ed269"
            date: "2022-03-24"
        }
    )  {
             _id
            doctorId
            patientName
            consultationId
            date
            fromTime
            toTime
            tz
    }
}

query {
    getConsultation(
            consultationId: "consul12rr345678790"
